INSERT INTO Card VALUES('clwbhuu6z0002u7wkgka7899b',1715999600988,1718875675321,'How did you save this card?','{"type":"doc","content":[{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Saving Changes"},{"type":"text","text":":"}]},{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The "},{"type":"text","marks":[{"type":"code"}],"text":"EditorWrapper"},{"type":"text","text":" component handles the rich text editor for the answer. When the user clicks the save button, the changes are saved to the server."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The "},{"type":"text","marks":[{"type":"code"}],"text":"CardDisplay"},{"type":"text","text":" component allows inline editing of the question. Changes are saved when the input loses focus or the user presses Enter."}]}]}]}]},{"type":"listItem","content":[{"type":"paragraph"}]}]}]}','','clw8lev490000p0dqwfyeybiv','clwbh5qrm0000u7wkkkmzm6gz');
INSERT INTO Card VALUES('clx5mjvgc0000kylpn05uloxt',1717821472765,1718937639148,'Describe your workflow in creating this project.  Pros? Cons?','{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"This is an interview preparation app where users can create and manage decks of cards. Each card contains a question and an answer, and users can edit these details. You''ve implemented a modal system to display and edit individual cards, and you are using Zustand for state management to ensure that changes are reflected across the application."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Components and Pages"}]},{"type":"orderedList","attrs":{"tight":true,"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"code"}],"text":"DeckIdPage"},{"type":"text","text":": Displays all cards within a specific deck. Users can click on a card to open it in a modal for editing."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"code"}],"text":"CardDisplay"},{"type":"text","text":": Displays the details of a card and allows inline editing of the question."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"code"}],"text":"EditorWrapper"},{"type":"text","text":": Provides a rich text editor for editing the answer of a card and includes a save button."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"code"}],"text":"CardModalContent"},{"type":"text","text":": Handles the content displayed within the modal for a specific card."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"code"}],"text":"Modal"},{"type":"text","text":": Handles the modal display logic."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Zustand Store ("},{"type":"text","marks":[{"type":"code"}],"text":"useCardStore"},{"type":"text","text":"): Manages the global state of cards, ensuring that changes are reflected across the application."}]}]}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"Workflow and Interactions"}]},{"type":"orderedList","attrs":{"tight":false,"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Initial Deck and Card Display"},{"type":"text","text":":"}]},{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The user navigates to a specific deck page ("},{"type":"text","marks":[{"type":"code"}],"text":"/decks/[deckId]"},{"type":"text","text":")."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"code"}],"text":"DeckIdPage"},{"type":"text","text":" fetches the deck data and the cards within the deck."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Each card is displayed using the "},{"type":"text","marks":[{"type":"code"}],"text":"CardDisplay"},{"type":"text","text":" component."}]}]}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Editing a Card"},{"type":"text","text":":"}]},{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The user clicks on a card in "},{"type":"text","marks":[{"type":"code"}],"text":"DeckIdPage"},{"type":"text","text":"."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The "},{"type":"text","marks":[{"type":"code"}],"text":"DeckCard"},{"type":"text","text":" component triggers a client-side navigation using Next.js router, updating the URL to "},{"type":"text","marks":[{"type":"code"}],"text":"/decks/[deckId]/c/[cardId]"},{"type":"text","text":" and opens the modal."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The "},{"type":"text","marks":[{"type":"code"}],"text":"CardModalContent"},{"type":"text","text":" component fetches the card details and displays them in the "},{"type":"text","marks":[{"type":"code"}],"text":"Modal"},{"type":"text","text":"."}]}]}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"State Management with Zustand"},{"type":"text","text":":"}]},{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The "},{"type":"text","marks":[{"type":"code"}],"text":"useCardStore"},{"type":"text","text":" manages the state of all cards. When a card is opened in the modal, its state is updated in the store."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Any changes made to the card (e.g., updating the question or answer) are saved to the server and updated in the Zustand store."}]}]}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Saving Changes"},{"type":"text","text":":"}]},{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The "},{"type":"text","marks":[{"type":"code"}],"text":"EditorWrapper"},{"type":"text","text":" component handles the rich text editor for the answer. When the user clicks the save button, the changes are saved to the server."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The "},{"type":"text","marks":[{"type":"code"}],"text":"CardDisplay"},{"type":"text","text":" component allows inline editing of the question. Changes are saved when the input loses focus or the user presses Enter."}]}]}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","marks":[{"type":"bold"}],"text":"Updating the Deck Page"},{"type":"text","text":":"}]},{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"When the modal is closed, the updated card data from the Zustand store is reflected in the "},{"type":"text","marks":[{"type":"code"}],"text":"DeckIdPage"},{"type":"text","text":" without needing a full page reload."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"The Zustand store ensures that the state is consistent across different parts of the application."}]}]}]}]}]},{"type":"heading","attrs":{"level":1},"content":[{"type":"text","text":"New Technologies to link:"}]},{"type":"bulletList","attrs":{"tight":true},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"React"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"NextJs"}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Databases"}]}]}]}]}','','clw8lev490000p0dqwfyeybiv','clwbh5qrm0000u7wkkkmzm6gz');
INSERT INTO Card VALUES('clx5mjvgd0001kylptxdh3gsj',1717821472765,1718858749250,'Why did you use Zustand for State Management?','{"type":"doc","content":[{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"1. "},{"type":"text","marks":[{"type":"bold"}],"text":"Simplicity and Minimal Boilerplate"}]},{"type":"paragraph","content":[{"type":"text","text":"Zustand is straightforward to set up and use, with minimal boilerplate code compared to other state management solutions like Redux. This simplicity makes it easy to maintain and scale your application."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"2. "},{"type":"text","marks":[{"type":"bold"}],"text":"Flexibility and Customization"}]},{"type":"paragraph","content":[{"type":"text","text":"Zustand allows you to define your state and actions in a very flexible manner. You can easily create custom logic for updating state and handling asynchronous operations, making it suitable for various use cases."}]},{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"3. "},{"type":"text","marks":[{"type":"bold"}],"text":"Performance"}]},{"type":"paragraph","content":[{"type":"text","text":"Zustand is optimized for performance. It uses a fast internal subscription mechanism and minimizes unnecessary re-renders by ensuring that only the components that depend on specific pieces of state will re-render when that state changes."}]},{"type":"heading","attrs":{"level":3}}]}','','clw8lev490000p0dqwfyeybiv','clwbh5qrm0000u7wkkkmzm6gz');
INSERT INTO Card VALUES('clxn3q6dr0000qwlw1r2yydry',1718878245328,1718881809981,'How did you get into software engineering?','{"type":"doc","content":[{"type":"paragraph","content":[{"type":"text","text":"My passion for programming sparked when I started to ask myself questions like:"}]},{"type":"blockquote","content":[{"type":"heading","attrs":{"level":3},"content":[{"type":"text","text":"We have "},{"type":"text","marks":[{"type":"underline"},{"type":"bold"}],"text":"A"},{"type":"text","text":" situation... How could I improve this? How could I innovate?"}]}]},{"type":"blockquote","content":[{"type":"paragraph","content":[{"type":"text","text":"What"},{"type":"text","marks":[{"type":"textStyle","attrs":{"color":"rgb(0, 138, 0)"}}],"text":" tools "},{"type":"text","text":"could I build for those in my immediate community?"}]}]},{"type":"blockquote","content":[{"type":"paragraph","content":[{"type":"text","text":"What problem or gap exists in a  "},{"type":"text","marks":[{"type":"textStyle","attrs":{"color":"#E00000"}}],"text":"work"},{"type":"text","text":" environment, or "},{"type":"text","marks":[{"type":"textStyle","attrs":{"color":"#2563EB"}}],"text":"workflow"},{"type":"text","text":", or specific "},{"type":"text","marks":[{"type":"textStyle","attrs":{"color":"rgb(186, 64, 129)"}}],"text":"market"},{"type":"text","text":", that could be addressed and solved with a program I could make? (ANY MARKET)"}]}]},{"type":"paragraph","content":[{"type":"text","text":"Simply label my questions into  categories: "}]},{"type":"orderedList","attrs":{"tight":true,"start":1},"content":[{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Helping others, specifically improving some aspect of their lives."}]}]},{"type":"listItem","content":[{"type":"paragraph","content":[{"type":"text","text":"Professionally: Improves the system structure or flow in some way."}]}]}]},{"type":"paragraph","content":[{"type":"text","text":"When I put myself into this mindset of helping others, identifying their needs, and solving problems."}]},{"type":"paragraph","content":[{"type":"text","text":"Tackling problems is FUN, programming just gives me the tools to overcome them.  And solving them could make the world better, even if it''s 1% better."}]},{"type":"paragraph","content":[{"type":"text","text":"I start to bring this mindset into every aspect of my life as humans should, but on a greater scale than never before."}]},{"type":"paragraph","content":[{"type":"text","text":"Don''t think like an engineer.  Be an engineer."}]},{"type":"paragraph"},{"type":"paragraph"},{"type":"paragraph"}]}','','clw8lev490000p0dqwfyeybiv','clwbh5qrm0000u7wkkkmzm6gz');
